func testCheckAzureRMTaskExists(resourceName string) resource.TestCheckFunc {
    return func(s *terraform.State) error {
        rs, ok := s.RootModule().Resources[resourceName]
        if !ok {
            return fmt.Errorf("Task not found: %s", resourceName)
        }

        groupName := rs.Primary.Attributes["group_name"]
        projectName := rs.Primary.Attributes["project_name"]
        serviceName := rs.Primary.Attributes["service_name"]
        taskName := rs.Primary.Attributes["task_name"]

        client := testAccProvider.Meta().(*ArmClient).tasksClient
        ctx := testAccProvider.Meta().(*ArmClient).StopContext

        if resp, err := client.Get(ctx, groupName, serviceName, projectName, taskName); err != nil {
            if utils.ResponseWasNotFound(resp.Response) {
                return fmt.Errorf("Bad: Task (Task Name %q / Project Name %q / Service Name %q / Group Name %q) does not exist", taskName, projectName, serviceName, groupName)
            }
            return fmt.Errorf("Bad: Get on tasksClient: %+v", err)
        }

        return nil
    }
}

