    frontendIpconfigurations := d.Get("frontend_ipconfigurations").([]interface{})
    frontendPorts := d.Get("frontend_ports").([]interface{})
    gatewayIpconfigurations := d.Get("gateway_ipconfigurations").([]interface{})
