func resourceArmApplicationGatewayDelete(d *schema.ResourceData, meta interface{}) error {
    client := meta.(*ArmClient).applicationGatewaysClient
    ctx := meta.(*ArmClient).StopContext


    id, err := azure.ParseAzureResourceID(d.Id())
    if err != nil {
        return err
    }
    resourceGroup := id.ResourceGroup
    applicationGatewayName := id.Path["applicationGateways"]

    future, err := client.Delete(ctx, resourceGroup, applicationGatewayName)
    if err != nil {
        if response.WasNotFound(future.Response()) {
            return nil
        }
        return fmt.Errorf("Error deleting Application Gateway (Application Gateway Name %q / Resource Group %q): %+v", applicationGatewayName, resourceGroup, err)
    }

    if err = future.WaitForCompletionRef(ctx, client.Client); err != nil {
        if !response.WasNotFound(future.Response()) {
            return fmt.Errorf("Error waiting for deleting Application Gateway (Application Gateway Name %q / Resource Group %q): %+v", applicationGatewayName, resourceGroup, err)
        }
    }

    return nil
}

