func testCheckAzureRMDdosProtectionPlanExists(resourceName string) resource.TestCheckFunc {
    return func(s *terraform.State) error {
        rs, ok := s.RootModule().Resources[resourceName]
        if !ok {
            return fmt.Errorf("Ddos Protection Plan not found: %s", resourceName)
        }

        resourceGroup := rs.Primary.Attributes["resource_group"]
        ddosProtectionPlanName := rs.Primary.Attributes["ddos_protection_plan_name"]

        client := testAccProvider.Meta().(*ArmClient).ddosProtectionPlansClient
        ctx := testAccProvider.Meta().(*ArmClient).StopContext

        if resp, err := client.Get(ctx, resourceGroup, ddosProtectionPlanName); err != nil {
            if utils.ResponseWasNotFound(resp.Response) {
                return fmt.Errorf("Bad: Ddos Protection Plan (Ddos Protection Plan Name %q / Resource Group %q) does not exist", ddosProtectionPlanName, resourceGroup)
            }
            return fmt.Errorf("Bad: Get on ddosProtectionPlansClient: %+v", err)
        }

        return nil
    }
}

